import React, {Component} from 'react'
import { Text, View, StatusBar, StyleSheet } from 'react-native'
import { Button } from 'react-native-elements';
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import Icon from 'react-native-vector-icons/Ionicons';
import IconA from 'react-native-vector-icons/FontAwesome';

import firebase from 'firebase';

const SettingOption = ({label, icon, onPress}) => {
    label = label.toUpperCase();
    return (
        <>
            <Button onPress={onPress} titleStyle={{color: "black", marginLeft: 5}} buttonStyle={webStyles.settingsButton} icon={<Icon name={icon} size={20} color="black"/>} title={label}/>
        </>
    );
}

class Account extends Component {
    constructor(props) {
        super(props);
        this.onSignOut = this.signOut.bind(this);
    }

    signOut() {
        firebase.auth().signOut()
          .catch((error) => {
            console.log(error);
          })
    }


    render() {

        const user = firebase.auth().currentUser;
        var displayName;
        var email;
        var photoUrl;
        var emailVerified;
        var uid;
        if (user) {
        // User is signed in.
            if (user != null) {
                displayName = user.displayName;
                email = user.email;
                photoUrl = user.photoURL;
                emailVerified = user.emailVerified;
                uid = user.uid;  
                // The user's ID, unique to the Firebase project. Do NOT use
                // this value to authenticate with your backend server, if
                // you have one. Use User.getToken() instead.
            }
        } else {
        }

        return(
            <View style={webStyles.body}>
                <Text>Logged in {displayName}, Email: {email}</Text>
                <View style={webStyles.accountCard}><Text>Nick Harty</Text></View>
                <View>
                    <SettingOption label="Edit Personal Information" icon="settings-outline"/>
                    <SettingOption label="Email Settings" icon="settings-outline"/>
                    <SettingOption label="Privacy and Security" icon="settings-outline"/>
                    <SettingOption label="Connected Services" icon="settings-outline"/>
                </View>
                <Button onPress={() => this.signOut()} containerStyle={webStyles.signOutContainer} titleStyle={{color: "rgb(68, 199, 188)", marginLeft: 5}} buttonStyle={webStyles.signOutButton} icon={<Icon name="log-out-outline" size={25} color="rgb(68, 199, 188)"/>} title="SIGN OUT"/>
                <StatusBar style="auto" backgroundColor="rgb(68, 199, 188)" barStyle="dark-content" />
            </View>
        )
    }

}

var webStyles = StyleSheet.create({
    body: {
        backgroundColor: "white",
        height: "100%",
        padding: 20,
    },
    accountCard: {
        "borderTopLeftRadius": 5,
        "borderTopRightRadius": 5,
        "borderBottomRightRadius": 5,
        "borderBottomLeftRadius": 5,
        padding: 10,
        marginBottom: 20,
        backgroundColor: "white",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 3,
        },
        shadowOpacity: 0.27,
        shadowRadius: 4.65,

        elevation: 6,
    },
    settingsButton: {
        "backgroundColor": "transparent",
        "borderTopLeftRadius": 5,
        "borderTopRightRadius": 5,
        "borderBottomRightRadius": 5,
        "borderBottomLeftRadius": 5,
        borderWidth: 0,
        height: 50,
        justifyContent: "flex-start",
    },
    signOutButton: {
        "backgroundColor": "transparent",
        "borderTopLeftRadius": 5,
        "borderTopRightRadius": 5,
        "borderBottomRightRadius": 5,
        "borderBottomLeftRadius": 5,
        borderWidth: 0,
        height: 50,
        justifyContent: "flex-start",
    },
    signOutContainer: {
        marginTop: 25,
        backgroundColor: "rgb(235, 255, 255)",
    },  
});

export default Account;
