import { StatusBar } from 'expo-status-bar';
import React, {Component, useEffect, usetst} from 'react';
import { StyleSheet, Text, TextInput, View, ImageBackground, Dimensions, Button, Animated, Easing, SafeAreaView, Keyboard} from 'react-native';
import {NavigationContainer} from '@react-navigation/native'
import { LinearGradient } from "expo-linear-gradient";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import { faUser, faLock } from '@fortawesome/free-solid-svg-icons'
import { createStackNavigator, TransitionSpecs, HeaderStyleInterpolators } from '@react-navigation/stack';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Ionicons from 'react-native-vector-icons/Ionicons';
import { SafeAreaProvider, useSafeAreaInsets, initialWindowMetrics } from 'react-native-safe-area-context';
import 'react-native-gesture-handler';

import * as Linking from 'expo-linking';

import firebase from 'firebase';

const window = Dimensions.get("window");
const screen = Dimensions.get("screen");



// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyD4suI2UA6Us7ezsvsRpjCO5vdoMheuNlg",
  authDomain: "medibound-87c8a.firebaseapp.com",
  projectId: "medibound-87c8a",
  storageBucket: "medibound-87c8a.appspot.com",
  messagingSenderId: "1047243459021",
  appId: "1:1047243459021:web:f8c1e9e4df6f5d18b61c84",
  measurementId: "G-4NJ8B9CSZ6"
};

if(firebase.apps.length === 0) {
  firebase.initializeApp(firebaseConfig)
}

import LandingScreen from "./components/auth/Landing";
import LoginScreen from "./components/auth/Login";
import RegisterScreen from "./components/auth/Register";

import HomeScreen from "./components/index/Home";
import ReportsScreen from "./components/index/Reports";
import DevicesScreen from "./components/index/Devices";
import AccountScreen from "./components/index/Account";
import { TouchableHighlight } from 'react-native-gesture-handler';


const Stack = createStackNavigator();

const prefix = Linking.createURL('/');

const MyTransition = {
  gestureDirection: 'horizontal',
  transitionSpec: {
    open: TransitionSpecs.TransitionIOSSpec,
    close: TransitionSpecs.TransitionIOSSpec,
  },
  headerStyleInterpolator: HeaderStyleInterpolators.forFade,
  cardStyleInterpolator: ({ current, next, layouts }) => {
    return {
      cardStyle: {
        transform: [
          {
            translateX: current.progress.interpolate({
              inputRange: [0, 1],
              outputRange: [layouts.screen.width, 0],
            }),
          },
          {
            scale: next
              ? next.progress.interpolate({
                  inputRange: [0, 1],
                  outputRange: [1, 0.9],
                })
              : 1,
          },
        ],
      },
      overlayStyle: {
        opacity: current.progress.interpolate({
          inputRange: [0, 1],
          outputRange: [0, 0.5],
        }),
      },
    };
  },
}

var showTopBar = true;

export class App extends Component {

  constructor(props) {
    super(props)
    this.state = {
      loaded: false,
      bounceValue: new Animated.Value(1),
      areResourcesReady: false,
    } 

    this.bounce.bind(this);
  }

  componentDidMount() {
    firebase.auth().onAuthStateChanged((user) => {
      if (!user) {
        this.setState({
          loggedIn: false,
          loaded: true,
        })
      } else {
        this.setState({
          loggedIn: true,
          loaded: true,
        })
      }
    })
  }
  signOut() {
    firebase.auth().signOut()
      .catch((err) => {
        console.log(error);
      })
  }

  bounce(x){
      console.log(x);
      this.state.bounceValue.setValue(1.1);
      Animated.spring(
        this.state.bounceValue, {
          useNativeDriver: true,
          toValue: 1,
          friction: 2,
        }
      ).start();
  }

  render() {
    const linking = {
      prefixes: [prefix],
      config: {
        screens: {
          Login: 'login',
          Register: 'join',
          Home: '',
          Devices: 'devices',
          Reports: 'reports',
          Account: 'account'
        }
      },
    };

    const Tab = createBottomTabNavigator();

    const {loggedIn, loaded} = this.state;

    if (Keyboard) {
      showTopBar = false
    }
    else {
      showTopBar = true;
    }

    if (!loaded) {
      return (
        <View style={{width: "100%", height: "100%", alignItems: "center", alignContent: "center", justifyContent: "center"}}>
        </View>

      );
    }
    if (!loggedIn) {
      return (
        <SafeAreaProvider initialMetrics={initialWindowMetrics}>
          <NavigationContainer linking={linking}>
            <Stack.Navigator initialRouteName="Login">
              <Stack.Screen name="Landing" component={LandingScreen} options={{ headerShown: false, headerBackTitleStyle: {color: "#000000"}} } navigation={this.props.navigation}/>
              <Stack.Screen name="Login" component={LoginScreen} options={{ headerShown: false, headerTitleStyle:{color: "black"}, headerBackTitleStyle: {color: "black"}} } navigation={this.props.navigation}/>
              <Stack.Screen name="Register" component={RegisterScreen} options={{ headerShown: showTopBar, ...MyTransition ,headerBackImage: () => <Ionicons style={{paddingLeft: 0}} name="chevron-back-circle-outline" size={36} color="rgb(68, 199, 188)"/>, headerBackTitleVisible: false, headerTransparent: true, headerTitle: false,} } navigation={this.props.navigation}/>
            </Stack.Navigator>
          </NavigationContainer>
        </SafeAreaProvider>

      );
    }

    return (
      <SafeAreaProvider initialMetrics={initialWindowMetrics}>
        <NavigationContainer linking={linking} >
          <Tab.Navigator 
          screenOptions={({ route }) => ({
            tabBarIcon: ({ focused, color, size }) => {
              let iconName;

              if (route.name === 'Home') {
                iconName = focused 
                  ? 'cube' 
                  : 'cube-outline';
              }
              else if (route.name === 'Devices') {
                iconName = focused 
                  ? 'layers' 
                  : 'layers-outline';
              }
              else if (route.name === 'Users') {
                iconName = focused
                  ? 'people'
                  : 'people-outline';
              } 
              else if (route.name === 'Account') {
                iconName = focused 
                  ? 'person' 
                  : 'person-outline';
              }

              // You can return any component that you like here!
              return (
                <Animated.View  style={{scaleX: this.state.bounceValue,scaleY: this.state.bounceValue}}>
                  <TouchableHighlight>
                    <Ionicons name={iconName} size={size} color={color} />
                  </TouchableHighlight>
                </Animated.View>);
            },
          })}
          tabBarOptions={{
            activeTintColor: 'rgb(68, 199, 188)',
            inactiveTintColor: '#555555',
            allowFontScaling: false,
            style: {border: 0, shadowColor: "white", borderTopWidth: 5, borderTopColor: "white", backgroundColor: "white", marginBottom: 5, elevation: 0},
            tabStyle: { },
            labelStyle: {fontSize: 12}
          }}
        >
            <Tab.Screen name="Home" component={HomeScreen}  />
            <Tab.Screen name="Devices" component={DevicesScreen} />
            <Tab.Screen name="Users" component={ReportsScreen} />
            <Tab.Screen name="Account" component={AccountScreen} />
          </Tab.Navigator>
        </NavigationContainer>
      </SafeAreaProvider>
    );
  }
}

export default App
