import React, {Component} from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TextInput, View, ImageBackground, Animated, Dimensions } from 'react-native';
import { Button } from 'react-native-elements';
import { LinearGradient } from "expo-linear-gradient";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import { faUser, faLock, faMailBulk, faEnvelope, faExclamationCircle, faKey } from '@fortawesome/free-solid-svg-icons';
import { NavigationContainer } from '@react-navigation/native';
import { render } from 'react-dom';
import Ionicons from 'react-native-vector-icons/Ionicons';

import firebase from 'firebase';
import { SafeAreaView } from 'react-native-safe-area-context';

const window = Dimensions.get("window");
const screen = Dimensions.get("screen");
 

const UserForm = ({accountType, item}) => {
  if (accountType == 1)  {
    return (
        <>
          <View style={styles.accountType}><Button containerStyle={{width: "50%"}} buttonStyle={styles.accountTypeButton} titleStyle={styles.accountTypeButtonText} title="CLIENT ACCOUNT" onPress={() => this.setState({accounttype: 0})}></Button><Button buttonStyle={styles.accountTypeButtonActive} containerStyle={{width: "50%"}} titleStyle={styles.accountTypeButtonTextActive} title="BUSINESS ACCOUNT" ></Button></View>
  
          <Ionicons name={"business"} style={styles.registerIcon} size={15} />
  
          <TextInput
              style={[styles.registerInput]}
              placeholder="Organization Name"
              keyboardType="default"
              onSubmitEditing={() => item.onSignUp()}
              autoCompleteType="username"
              onChangeText={(username) => item.setState({username: username.trim()})}
            />
  
        </>
    );
  }
  else if (accountType == 0) {
    return (
        <>
          <View style={styles.accountType}><Button containerStyle={{width: "50%"}} buttonStyle={styles.accountTypeButtonActive} titleStyle={styles.accountTypeButtonTextActive} title="CLIENT ACCOUNT"></Button><Button buttonStyle={styles.accountTypeButton} containerStyle={{width: "50%"}} titleStyle={styles.accountTypeButtonText} title="BUSINESS ACCOUNT" onPress={() => this.setState({accounttype: 1})}></Button></View>
  
          <Ionicons name={"person"} style={styles.registerIcon} size={15} />
  
          <View style={styles.registerInputNameStyle}>
            <TextInput
              style={styles.registerInputName}
              placeholder="First Name"
              keyboardType="default"
              onSubmitEditing={() => item.onSignUp()}
              autoCompleteType="off"
              onChangeText={(username) => item.setState({username: username.trim()})}
            />
  
            <TextInput
              style={[styles.registerInputName, {paddingLeft: 20}]}
              placeholder="Last Name"
              keyboardType="default"
              onSubmitEditing={() => this.onSignUp()}
              autoCompleteType="off"
              onChangeText={(username) => this.setState({username: username.trim()})}
            />
          </View>
          
  
        </>
    );
  }
}

class Register extends Component {

  constructor(props) {
    super(props);

    this.state = {
      email: '',
      username: '',
      firstname: '',
      lastname: '',
      password: '',
      passwordconfirm: '',
      accounttype: 0,
      errorMessage: '',
      fadeError: new Animated.Value(0),
      dimensions: {
        window,
        screen
      }
    }

    this.onSignUp = this.onSignUp.bind(this);
  }

  onSignUp() {
    const { email,username, firstname, lastname, accounttype, password, passwordconfirm  } = this.state;
    if (username.trim() != "") {
      if (passwordconfirm == password) {
        firebase.auth().createUserWithEmailAndPassword(email, password)
            .then((result) => {
                firebase.firestore().collection("users")
                    .doc(firebase.auth().currentUser.uid)
                    .set({
                        username,
                        email
                    })
                firebase.auth().currentUser.updateProfile({displayName: username})
                console.log(result);
            })
            .catch((error) => {
                console.log(error);
                this.setState({errorMessage: error.message});
                Animated.timing(this.state.fadeError, {
                  toValue: 1,
                  duration: 1000,
                  useNativeDriver: true,
                }).start();
            })
        }
        else {
          this.setState({errorMessage: "Passwords Must Match"});
          Animated.timing(this.state.fadeError, {
            toValue: 1,
            duration: 1000,
            useNativeDriver: true,
          }).start();
      }
    }
    else {
        this.setState({errorMessage: "A full name is required for sign up"});
        Animated.timing(this.state.fadeError, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }).start();
    }
  }

  onChange = ({ window, screen }) => {
    this.setState({ dimensions: { window, screen } });
    if (window.width < 801) {
      styles = StyleSheet.flatten([webStyles,mobileStyles]);
    }
    else {
      styles = webStyles;
    }
  };

  componentDidMount() {
    Dimensions.addEventListener("change", this.onChange);
  }

  componentWillUnmount() {
    Dimensions.removeEventListener("change", this.onChange);
  }

  
  render() {

    

    return (
      
      <SafeAreaView style={{backgroundColor: "white", flex: 1}}>
      <ImageBackground source={require('../../src/assets/img/hex.jpg')} style={styles.body}>    
        <LinearGradient colors={['rgba(255, 255, 255, 0.7)', 'rgba(255, 255, 255, 1)']} style={styles.linearGradient}>
          <View style={styles.innerbody}>

            <View style={styles.register}>
              <View style={styles.registerTitle}><Text style={{"fontSize" : 20,"fontWeight" : "600", "letterSpacing" : 3, color: "#777777"}}>CREATE AN<Text style={{color: "#777777", fontWeight: "600"}}> ACCOUNT</Text> </Text></View>
              <UserForm accountType={1}/>
              
              <Ionicons name={"mail"} style={styles.registerIcon} size={15} />
              <TextInput
                style={styles.registerInput}
                placeholder="Email"
                keyboardType="email-address"
                onSubmitEditing={() => this.onSignUp()}
                autoCompleteType="email"
                onChangeText={(email) => this.setState({email: email})}
              />
              <Ionicons name={"lock-closed"} style={styles.registerIcon} size={15} />
              <TextInput
                style={styles.registerInput}
                placeholder="Password"
                keyboardType="default"
                onSubmitEditing={() => this.onSignUp()}
                autoCompleteType="password"
                secureTextEntry={true}
                onChangeText={(password) => this.setState({password: password})}
              />
              <Ionicons name={"lock-closed-outline"} style={styles.registerIcon} size={15} />
              <TextInput
                style={styles.registerInput}
                placeholder="Confirm Password"
                keyboardType="default"
                onSubmitEditing={() => this.onSignUp()}
                autoCompleteType="password"
                secureTextEntry={true}
                onChangeText={(passwordconfirm) => this.setState({passwordconfirm: passwordconfirm})}
              />
      
              <Animated.View style={[styles.error, {opacity: this.state.fadeError}, {display: this.state.displayError}]} >
                <FontAwesomeIcon style={styles.errorIcon} icon={ faExclamationCircle } />
                <Text style={{color: "#d36e6e", fontWeight: "400",padding: 5}}>{this.state.errorMessage}</Text>
              </Animated.View>
      
              <Button titleStyle={{color: "#777777",fontSize: 12,textDecorationLine:"underline"}} buttonStyle={styles.flipButton} onPress={() => this.props.navigation.navigate("Login")} color="black" title="HAVE AN ACCOUNT? LOG IN"/>
      
              <Button titleStyle={{fontWeight: "600", fontSize:16,justifyContent:"center",alignContent:"center"}} containerStyle={styles.registerButtonContainer} buttonStyle={styles.registerButton} 
                  title="SIGN UP"
                  onPress={() => this.onSignUp()}
                  color="transparent"/>

            </View>

            <View style={styles.registerimg}>
              <ImageBackground source={require('../../src/assets/img/hex.jpg')} style={styles.image}>
                <LinearGradient colors={['rgba(74, 191, 217, 0.7)', 'rgba(114, 212, 192,1)']} style={styles.linearGradient}>
                  
                </LinearGradient>
              </ImageBackground>
            </View>

            <StatusBar style="auto" backgroundColor="rgb(68, 199, 188)" barStyle="dark-content" />
          </View>
        </LinearGradient>
      </ImageBackground>
      </SafeAreaView>
    )
  }
}



