import React, {Component} from 'react'
import { Text, View, StatusBar, StyleSheet } from 'react-native'
import { Button } from 'react-native-elements';
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import Icon from 'react-native-vector-icons/Ionicons';
import IconA from 'react-native-vector-icons/FontAwesome';

import firebase from 'firebase';

class Account extends Component {
    constructor(props) {
        super(props);
        this.onSignOut = this.signOut.bind(this);
    }

    signOut() {
        firebase.auth().signOut()
          .catch((error) => {
            console.log(error);
          })
    }



    render() {

        const user = firebase.auth().currentUser;
        if (user) {
        // User is signed in.
            if (user != null) {
                const displayName = user.displayName;
                const email = user.email;
                const photoUrl = user.photoURL;
                const emailVerified = user.emailVerified;
                const uid = user.uid;  
                // The user's ID, unique to the Firebase project. Do NOT use
                // this value to authenticate with your backend server, if
                // you have one. Use User.getToken() instead.
            }
        } else {
        }

        return(
            <View style={webStyles.body}>
                <Text>Logged in {displayName}</Text>
                <Button onPress={() => { this.signOut()}}  titleStyle={{color: "rgb(68, 199, 188)", marginLeft: 5}} buttonStyle={webStyles.signOutButton} icon={<Icon name="log-out-outline" size={20} color="rgb(68, 199, 188)"/>} title="SIGN OUT"/>
                <StatusBar style="auto" backgroundColor="rgb(68, 199, 188)" barStyle="dark-content" />
            </View>
        )
    }

}

var webStyles = StyleSheet.create({
    body: {
        backgroundColor: "white",
        height: "100%",
        padding: 20,
    },
    signOutButton: {
        "backgroundColor": "transparent",
        borderWidth: 1,
        borderColor: "rgb(68, 199, 188)",
    },
});

export default Account;
