import React, {Component} from 'react'
import { Text, View, StatusBar, StyleSheet } from 'react-native'
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import { Button } from 'react-native-elements';
import Icon from 'react-native-vector-icons/Ionicons';
import IconA from 'react-native-vector-icons/FontAwesome';

import firebase from 'firebase';
import { ScrollView } from 'react-native-gesture-handler';
import { useScrollToTop } from '@react-navigation/native';

const SettingOption = ({label, icon, onPress}) => {
    label = label;
    return (
        <>
        <View style={{flexDirection: "row", width: "100%"}}>
            <Button onPress={onPress} titleStyle={{color: "#555555", marginLeft: 10, fontSize: 17, fontWeight: "100", textAlign: "left"}} buttonStyle={webStyles.settingsButton} icon={<Icon name={icon} size={25} color="#555555"/>} title={label}/> 
            <Icon style={{marginTop: 11, position: "absolute", right: 0}} name={icon} size={25} color="#555555"/>
        </View>
        </>
    );
}


class Account extends Component {
    constructor(props) {
        super(props);
        this.state = {
            account: "",
        }
        this.onSignOut = this.signOut.bind(this);
        this.accountype = ""
        this.data = firebase.firestore().collection("users").doc(firebase.auth().currentUser.uid).onSnapshot(doc => {
            this.setState({
                account: doc.data().accounttype,
            })
        });
    }


    signOut() {
        firebase.auth().signOut()
          .catch((error) => {
            console.log(error);
          })
    }



    render() {

        const user = firebase.auth().currentUser;
        var displayName;
        var email;
        var photoUrl;
        var emailVerified;
        var uid;
        var accounttype = this.state.account;
        if (user) {
        // User is signed in.
            if (user != null) {
                displayName = user.displayName;
                email = user.email;
                photoUrl = user.photoURL;
                emailVerified = user.emailVerified;
                uid = user.uid;  
                // The user's ID, unique to the Firebase project. Do NOT use
                // this value to authenticate with your backend server, if
                // you have one. Use User.getToken() instead.
            }
        } else {
        }

        return(
            <ScrollView style={webStyles.body}>
                <SafeAreaView style={{padding: 0}} >
                    <View style={webStyles.accountCard}><Text>{displayName} Account Type: {accounttype}</Text></View>
                    <View style={webStyles.card}>
                        <SettingOption label="Personal Information" icon="person-circle-outline"/>
                        <SettingOption label="Email Settings" icon="mail-outline"/>
                        <SettingOption label="Privacy and Security" icon="lock-closed-outline"/>
                        <SettingOption label="Connected Services" icon="globe-outline"/>
                        <SettingOption label="Help" icon="help-buoy-outline"/>
                        <SettingOption label="About" icon="information-circle-outline"/>
                        <Button onPress={() => this.signOut()} containerStyle={webStyles.signOutContainer} titleStyle={{color: "rgb(68, 199, 188)", fontSize: 17, marginLeft: 10, width: "100%"}} buttonStyle={webStyles.settingsButton} icon={<Icon name="log-out-outline" size={25} color="rgb(68, 199, 188)"/>} title="SIGN OUT"/>
                    </View>
                    <StatusBar style="auto" backgroundColor="rgb(68, 199, 188)" barStyle="dark-content" />
                </SafeAreaView>
            </ScrollView>
        )
    }

}

var webStyles = StyleSheet.create({
    body: {
        backgroundColor: "#fbfbfb",
        height: "100%",
        width: "100%",
        overflow: "scroll",
        padding: 20,
    },
    accountCard: {
        "borderTopLeftRadius": 5,
        "borderTopRightRadius": 5,
        "borderBottomRightRadius": 5,
        "borderBottomLeftRadius": 5,
        padding: 0,
        width: "100%",
        marginBottom: 20,
    },
    card: {
        padding: 0,
        shadowColor: "#000",
        width: "100%",
        shadowOffset: {
            width: 0,
            height: 3,
        },
        shadowOpacity: 0.15,
        shadowRadius: 4.65,

        elevation: 3,
    },
    settingsButton: {
        "backgroundColor": "transparent",
        "borderTopLeftRadius": 5,
        "borderTopRightRadius": 5,
        "borderBottomRightRadius": 5,
        "borderBottomLeftRadius": 5,
        width: "100%",
        borderWidth: 0,
        borderBottomWidth: 1,
        borderBottomColor: "#dddddd",
        height: 50,
        justifyContent: "flex-start",
    },
    signOutButton: {
        "backgroundColor": "transparent",
        "borderTopLeftRadius": 5,
        "borderTopRightRadius": 5,
        "borderBottomRightRadius": 5,
        "borderBottomLeftRadius": 5,
        borderWidth: 1,
        borderColor: "rgb(68, 199, 188)", 
        justifyContent: "flex-start",
        height: 50,
    },
    signOutContainer: {
        backgroundColor: "white",
    },  
});

export default Account;
