import React, {Component} from 'react'
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import { Text, View, StatusBar, StyleSheet, Dimensions } from 'react-native'
import {Button} from 'react-native-elements';
import Icon from 'react-native-vector-icons/Ionicons';
import BottomSheet from 'react-native-bottomsheet-reanimated';

const Screen = {
    width: Dimensions.get('window').width,
    height: Dimensions.get('window').height,
  };
const snapPoints = [0, Screen.height / 2, '70%', '100%'];

class Devices extends Component {

    onOpenBottomSheetHandler = (index) => {
        this.refs.BottomSheet.snapTo(index);
    };

    render() {
        return(
            <SafeAreaView style={deviceStyles.body}>
                <View style={[deviceStyles.topBarButtons,{"borderBottomColor": "#dddddd","borderBottomWidth": 1,}]}>
                    <Button titleStyle={{fontWeight: "600", fontSize:16,justifyContent:"center",alignContent:"center",marginLeft: 5,}} buttonStyle={deviceStyles.greenButton} containerStyle={{flex: 0.48}} icon={<Icon name="keypad" size={15} color="white" />} title="ENTER CODE"></Button>
                    <Button titleStyle={{fontWeight: "600", color: "#777777", fontSize:16,justifyContent:"center",alignContent:"center",marginLeft: 5,}} buttonStyle={deviceStyles.grayButton} containerStyle={{flex: 0.48}} icon={<Icon name="radio" size={15} color="#777777"/>} title="NFC TO ADD"></Button>
                </View>

                <BottomSheet
                    keyboardAware
                    bottomSheerColor="#FFFFFF"
                    ref="BottomSheet"
                    initialPosition={'50%'} //200, 300
                    snapPoints={['50%', '100%']}
                    isBackDrop={true}
                    isBackDropDismissByPress={true}
                    isRoundBorderWithTipHeader={true}
                    // backDropColor="red"
                    // isModal
                    // containerStyle={{backgroundColor:"red"}}
                    // tipStyle={{backgroundColor:"red"}}
                    // headerStyle={{backgroundColor:"red"}}
                    // bodyStyle={{backgroundColor:"red",flex:1}}
                    header={
                        <View>
                        <Text>Header</Text>
                        </View>
                    }
                    body={
                        <View>
                        <Text>Body</Text>
                        </View>
                    }
                    />

                <StatusBar style="auto" backgroundColor="rgb(68, 199, 188)" barStyle="dark-content" />
            </SafeAreaView>
        )
    }

}

var deviceStyles = StyleSheet.create({
    body: {
        backgroundColor: "#fbfbfb",
        height: "100%",
    },
    topBarButtons: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        backgroundColor: 'white',
        paddingHorizontal: 20,
        paddingVertical: 15,
        width: "100%",
    },
    greenButton: {
        "paddingTop": 2.5,
      "paddingRight": 2.5,
      "paddingBottom": 2.5,
      "paddingLeft": 2.5,
      "backgroundColor": "rgb(68, 199, 188)",
      "shadowOffset": {
        "width": 0,
        "height": 0
      },
      "shadowRadius": 30,
      "shadowColor": "rgba(150, 255, 246, 0.7)",
      "shadowOpacity": 1,
      "borderStyle": "solid",
      "color": "white",
      "fontFamily": "sfd",
      "fontSize": 13,
      "fontWeight": "400",
      borderRadius: 5,
      "height": 40

    },
    grayButton: {
        "paddingTop": 2.5,
        "paddingRight": 2.5,
        "paddingBottom": 2.5,
        "paddingLeft": 2.5,
        "backgroundColor": "#fbfbfb",
        "shadowOffset": {
          "width": 0,
          "height": 0
        },
        "shadowRadius": 30,
        "shadowColor": "rgba(255, 255, 255, 0.7)",
        "shadowOpacity": 1,
        "borderWidth": 1,
        "borderColor": "#777777",
        "borderStyle": "solid",
        "color": "black",
        "fontFamily": "sfd",
        "fontSize": 13,
        "fontWeight": "400",
        borderRadius: 5,
        "height": 40
    }
});


export default Devices;
