import React, {Component} from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TextInput, View, ImageBackground, Animated, Dimensions } from 'react-native';
import { Button } from 'react-native-elements';
import { LinearGradient } from "expo-linear-gradient";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import { faUser, faLock, faMailBulk, faEnvelope, faExclamationCircle } from '@fortawesome/free-solid-svg-icons';
import { NavigationContainer } from '@react-navigation/native';
import { render } from 'react-dom';


import firebase from 'firebase';
import { SafeAreaView } from 'react-native-safe-area-context';

const window = Dimensions.get("window");
const screen = Dimensions.get("screen");


class Register extends Component {

  constructor(props) {
    super(props);

    this.state = {
      email: '',
      username: '',
      password: '',
      errorMessage: '',
      fadeError: new Animated.Value(0),
      dimensions: {
        window,
        screen
      }
    }

    this.onSignUp = this.onSignUp.bind(this);
  }

  onSignUp() {
    const { email,username, password  } = this.state;
    if (username.trim() != "") {
      firebase.auth().createUserWithEmailAndPassword(email, password)
          .then((result) => {
              firebase.firestore().collection("users")
                  .doc(firebase.auth().currentUser.uid)
                  .set({
                      username,
                      email
                  })
              firebase.auth().currentUser.updateProfile({displayName: username})
              console.log(result);
          })
          .catch((error) => {
              console.log(error);
              this.setState({errorMessage: error.message});
              Animated.timing(this.state.fadeError, {
                toValue: 1,
                duration: 1000,
                useNativeDriver: true,
              }).start();
          })
    }
    else {
        this.setState({errorMessage: "A full name is required for sign up"});
        Animated.timing(this.state.fadeError, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }).start();
    }
  }

  onChange = ({ window, screen }) => {
    this.setState({ dimensions: { window, screen } });
    if (window.width < 801) {
      styles = StyleSheet.flatten([webStyles,mobileStyles]);
    }
    else {
      styles = webStyles;
    }
  };

  componentDidMount() {
    Dimensions.addEventListener("change", this.onChange);
  }

  componentWillUnmount() {
    Dimensions.removeEventListener("change", this.onChange);
  }

  
  render() {
    return (
      
      <SafeAreaView style={{backgroundColor: "white", flex: 1}}>
      <ImageBackground source={require('../../src/assets/img/hex.jpg')} style={styles.body}>    
        <LinearGradient colors={['rgba(255, 255, 255, 0.7)', 'rgba(255, 255, 255, 1)']} style={styles.linearGradient}>
          <View style={styles.innerbody}>

            <View style={styles.register}>
              <View style={styles.registerTitle}><Text style={{"fontSize" : 20,"fontWeight" : "600", "letterSpacing" : 3, color: "#777777"}}>CREATE AN ACCOUNT</Text></View>

              <FontAwesomeIcon style={styles.registerIcon} icon={ faUser } />
              <TextInput
                style={styles.registerInput}
                placeholder="Full Name (Ex. First Name Last Name)"
                keyboardType="default"
                onSubmitEditing={() => this.onSignUp()}
                autoCompleteType="username"
                onChangeText={(username) => this.setState({username: username.trim()})}
              />
              <FontAwesomeIcon style={styles.registerIcon} icon={ faEnvelope } />
              <TextInput
                style={styles.registerInput}
                placeholder="Email"
                keyboardType="email-address"
                onSubmitEditing={() => this.onSignUp()}
                autoCompleteType="email"
                onChangeText={(email) => this.setState({email: email})}
              />
              <FontAwesomeIcon style={styles.registerIcon} icon={ faLock } />
              <TextInput
                style={styles.registerInput}
                placeholder="Password"
                keyboardType="default"
                onSubmitEditing={() => this.onSignUp()}
                autoCompleteType="password"
                secureTextEntry={true}
                onChangeText={(password) => this.setState({password: password})}
              />

              <Animated.View style={[styles.error, {opacity: this.state.fadeError}, {display: this.state.displayError}]} >
                <FontAwesomeIcon style={styles.errorIcon} icon={ faExclamationCircle } />
                <Text style={{color: "#d36e6e", fontWeight: "400",padding: 5}}>{this.state.errorMessage}</Text>
              </Animated.View>

              <Button titleStyle={{color: "#777777",fontSize: 12,textDecorationLine:"underline"}} buttonStyle={styles.flipButton} onPress={() => this.props.navigation.navigate("Login")} color="black" title="HAVE AN ACCOUNT? LOG IN"/>

              <Button titleStyle={{fontWeight: "600", fontSize:16,justifyContent:"center",alignContent:"center"}} containerStyle={styles.registerButtonContainer} buttonStyle={styles.registerButton} 
                  title="SIGN UP"
                  onPress={() => this.onSignUp()}
                  color="transparent"/>

            </View>

            <View style={styles.registerimg}>
              <ImageBackground source={require('../../src/assets/img/hex.jpg')} style={styles.image}>
                <LinearGradient colors={['rgba(74, 191, 217, 0.7)', 'rgba(114, 212, 192,1)']} style={styles.linearGradient}>
                  
                </LinearGradient>
              </ImageBackground>
            </View>

            <StatusBar style="auto" backgroundColor="rgb(68, 199, 188)" barStyle="dark-content" />
          </View>
        </LinearGradient>
      </ImageBackground>
      </SafeAreaView>
    )
  }
}

const webStyles = StyleSheet.create({
    image: {
      flex: 1,
      resizeMode: "cover",
      justifyContent: "center"
    },
  
    linearGradient: {
      width: '100%',
      height: '100%',
      opacity: 1,
      justifyContent: 'center',
      alignItems: 'center',
      "paddingTop": 40,
      "paddingRight": 40,
      "paddingBottom": 40,
      "paddingLeft": 40,
    },
  
    body: {
      "marginTop": 0,
      "marginRight": 0,
      "marginBottom": 0,
      "marginLeft": 0,
      "display": "flex",
      "width": "100%",
      "height": "100%",
      "flexDirection": "row",
      "flexWrap": "wrap",
      "alignItems": "center",
      "justifyContent": "center",
      "fontFamily" : "SFCompactDisplay-SemiBold",
    },
  
    innerbody: {
      "flexDirection": "row",
      "width": 800,
      "height": 500,
      "alignSelf": "center",
      "justifyContent": "center",
      "borderTopLeftRadius": 10,
      "borderTopRightRadius": 10,
      "backgroundColor": "white",
      "borderBottomRightRadius": 10,
      "borderBottomLeftRadius": 10,
      "shadowOffset": {
        "width": 0,
        "height": 0
      },
      "shadowRadius": 50,
      "shadowColor": "rgba(0,0,0,0.15)",
      "shadowOpacity": 1,
      "overflow": "hidden",
      "fontSize": 13
    },
  
    registerimg: {
      "width": "40%",
      "zIndex": -1,
    },
  
    register: {
      "flexDirection": "column",
      "alignContent": "center",
      "justifyContent": "center",
      "textAlign": "center",
      "alignItems": "center",
      "backgroundColor": "white",
      "width": "60%",
      "paddingTop": 40,
      "paddingRight": 40,
      "paddingBottom": 40,
      "paddingLeft": 40
    },
  
    registerTitle: {
      "display": "flex",
      "flexDirection": "row",
      "alignContent": "center",
      "justifyContent": "center",
      "textAlign": "center",
      "alignItems": "center",
      "marginBottom" : 30,
    },
  
    registerInput: {
      "marginBottom": 10,
      "paddingTop": 2.5,
      "paddingRight": 2.5,
      "paddingBottom": 2.5,
      "paddingLeft": 40,
      "backgroundColor": "#f4f8f7",
      "borderWidth": 0,
      "borderColor": "black",
      "borderStyle": "solid",
      "borderTopLeftRadius": 5,
      "borderTopRightRadius": 5,
      "borderBottomRightRadius": 5,
      "borderBottomLeftRadius": 5,
      "width": "100%",
      "height": 50
    },
  
    registerIcon: {
      "width": 12,
      "zIndex": 10,
      "left" : 15,
      "position" : "relative",
      "top": 34,
      "marginTop":-15,
      "alignSelf": "flex-start",
      "color": "#b0b4b3"
    },
  
    registerForm: {
      "flexDirection": "column",
      "textAlign": "center",
      "alignItems": "center",
      "width": "100%"
    },
  
    registerButton: {
    "paddingTop": 2.5,
    "paddingRight": 2.5,
    "paddingBottom": 2.5,
    "paddingLeft": 2.5,
    "backgroundColor": "rgb(68, 199, 188)",
    "shadowOffset": {
      "width": 0,
      "height": 0
    },
    "shadowRadius": 30,
    "shadowColor": "rgba(150, 255, 246, 0.7)",
    "shadowOpacity": 1,
    "borderWidth": 0,
    "borderColor": "black",
    "borderStyle": "solid",
    "color": "white",
    "fontFamily": "sfd",
    "fontSize": 13,
    "fontWeight": "400",
    "borderTopLeftRadius": 5,
    "borderTopRightRadius": 5,
    "borderBottomRightRadius": 5,
    "borderBottomLeftRadius": 5,
    "width": 150,
    "height": 40
    },

    registerButtonContainer: {
      width: 150,
    },

    error: {
      display: "none",
      "justifyContent" : "center",
      "paddingLeft": 35,
      "backgroundColor": "#ffe0e0",
      "textAlign": "left",
      "lineHeight": 25,
      "borderWidth": 0,
      "borderColor": "black",
      "borderStyle": "solid",
      "borderTopLeftRadius": 5,
      "borderTopRightRadius": 5,
      "borderBottomRightRadius": 5,
      "borderBottomLeftRadius": 5,
      "width": "100%",
      "height": "auto",
      "color": "#d36e6e",
      "overflow": "hidden",
      "fontFamily": "sfd-light",
      "fontWeight": "400"
    },

    errorIcon: {
      "width": 13,
      "zIndex": 2,
      "position": "absolute",
      "top" : 7,
      "left" : 10,
      "color": "#d36e6e"
    },

    flipButton: {
      "backgroundColor" : "transparent",
      height: 35,
    }
  });

  const mobileStyles = StyleSheet.create({
    linearGradient: {
      width: '100%',
      height: '100%',
      opacity: 1,
      justifyContent: 'center',
      alignItems: 'center',
      "paddingTop": 0,
      "paddingRight": 0,
      "paddingBottom": 0,
      "paddingLeft": 0,
    },
    register: {
      "flexDirection": "column",
      "alignContent": "center",
      "justifyContent": "center",
      "textAlign": "center",
      "alignItems": "center",
      "backgroundColor": "white",
      "width": "90%",
      "paddingTop": 0,
      "paddingRight": 0,
      "paddingBottom": 0,
      "paddingLeft": 0
    },
    registerimg: {
      display: "none",
    },
    registerInput: {
      "marginBottom": 10,
      "paddingTop": 2.5,
      "paddingRight": 2.5,
      "paddingBottom": 2.5,
      "paddingLeft": 40,
      "backgroundColor": "#f4f8f7",
      "borderWidth": 0,
      "borderColor": "black",
      "borderStyle": "solid",
      "borderTopLeftRadius": 5,
      "borderTopRightRadius": 5,
      "borderBottomRightRadius": 5,
      "borderBottomLeftRadius": 5,
      "width": "100%",
      "height": 55
    },
    registerButton: {
      "paddingTop": 2.5,
      "paddingRight": 2.5,
      "paddingBottom": 2.5,
      "paddingLeft": 2.5,
      "backgroundColor": "rgb(68, 199, 188)",
      "shadowOffset": {
        "width": 0,
        "height": 0
      },
      "shadowRadius": 30,
      "shadowColor": "rgba(150, 255, 246, 0.7)",
      "shadowOpacity": 1,
      "borderWidth": 0,
      "borderColor": "black",
      "borderStyle": "solid",
      "color": "white",
      "fontFamily": "sfd",
      "fontSize": 13,
      "fontWeight": "400",
      "borderTopLeftRadius": 5,
      "borderTopRightRadius": 5,
      "borderBottomRightRadius": 5,
      "borderBottomLeftRadius": 5,
      "width": "100%",
      "height": 45
    },
    registerButtonContainer: {
      "width": "100%",
    },
    innerbody: {
      "flexDirection": "row",
      "width": "100%",
      "height": "100%",
      "alignSelf": "center",
      "justifyContent": "center",
      "borderTopLeftRadius": 10,
      "borderTopRightRadius": 10,
      "backgroundColor": "white",
      "borderBottomRightRadius": 10,
      "borderBottomLeftRadius": 10,
      "shadowOffset": {
        "width": 0,
        "height": 0
      },
      "shadowRadius": 50,
      "shadowColor": "rgba(0,0,0,0.15)",
      "shadowOpacity": 1,
      "overflow": "hidden",
      "fontSize": 13
    }
  });

  var styles = webStyles;

  if (window.width < 801) {
    styles = StyleSheet.flatten([webStyles,mobileStyles]);
  }
  else {
    styles = webStyles;
  }

  export default Register;
